package io.github.robothy.sdwebui.sdk.utils;

import java.nio.file.Files;
import java.nio.file.Path;
import java.nio.file.Paths;
import java.nio.file.StandardOpenOption;
import org.junit.jupiter.api.Test;

class ImageConverterTest {

  @Test
  void toBase64PNG() throws Exception {
    String base64Img = "";

    String converted = ImageConverter.toBase64PNG(base64Img);
    String imgTag = "<img src=\"data:image/png;base64," + converted + "\" />";
    Path html = Files.createFile(Paths.get("a.html"));
    Files.writeString(html, imgTag, StandardOpenOption.TRUNCATE_EXISTING);
  }
}